import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View } from 'react-native';

export default function App() {
  return (
    // aparent, pentru a stiliza se foloseste sintaxa:
    // style={styles.container}
    // styles => un soi de variabila
    // container => contine regulile de styling pentru "rubrica" respectiva
                //  "container" fiind doar un nume, gen class in CSS
    // cool!
    <View style={styles.container}>
      <Text style={styles.experiment}>Open up App.js to start working on your app!</Text>
      <StatusBar style="auto" />
    </View>
  );
}


const styles = StyleSheet.create({
  // style pentru container, dha
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  // experiment
  experiment: {
    backgroundColor: '#f0f',
  },
});
